const reviews_summary = (data: any[] = []) => {
  const averages = {
    'Excellent': 0,
    'Very good': 0,
    'Average': 0,
    'Poor': 0,
    'Terrible': 0
  }
  const summary = [
    {
      "rating": 0,
      "times": 0,
      "ratingLabelLocalizedString": "Value"
    },
    {
      "rating": 0,
      "times": 0,
      "ratingLabelLocalizedString": "Rooms"
    },
    {
      "rating": 0,
      "times": 0,
      "ratingLabelLocalizedString": "Location"
    },
    {
      "rating": 0,
      "times": 0,
      "ratingLabelLocalizedString": "Cleanliness"
    },
    {
      "rating": 0,
      "times": 0,
      "ratingLabelLocalizedString": "Service"
    },
    {
      "rating": 0,
      "times": 0,
      "ratingLabelLocalizedString": "Sleep Quality"
    }
  ];

  if (Array.isArray(data.length) && data.length < 1)
    return summary;

  let total = 0;
  for (let i = 0; i < data.length; i++) { // for each review
    const mainrating = data[i]['rating']

    switch (mainrating) {
      case 5:
        averages['Excellent']++;
        break;
      case 4:
        averages['Very good']++;
        break;
      case 3:
        averages['Average']++;
        break;
      case 2:
        averages['Poor']++;
        break;
      default:
        averages['Terrible']++;
    }


    const additional = data[i]['additionalRatings'];

    total += mainrating;

    for (let b = 0; b < additional.length; b++) {
      summary[b]['rating'] += additional[b]['rating'];
      summary[b]['times']++;
    };
  }

  const rating = (total / data.length);

  for (let t = 0; t < summary.length; t++) {
    summary[t]['rating'] = (summary[t]['rating'] / summary[t]['times']);
    //total += summary[t]['rating'];
  }

  return {
    summary, averages, total, rating
  };

}

